mapper.insert:

insert into menu (parent_id, menu_name, url, 
  perms, type, order_num, 
  create_time, modify_time, method
  )
values (#{parentId,jdbcType=INTEGER}, #{menuName,jdbcType=VARCHAR}, #{url,jdbcType=VARCHAR}, 
  #{perms,jdbcType=VARCHAR}, #{type,jdbcType=CHAR}, #{orderNum,jdbcType=INTEGER}, 
  #{createTime,jdbcType=TIMESTAMP}, #{modifyTime,jdbcType=TIMESTAMP}, #{method,jdbcType=VARCHAR}
  )

mapper.select:
select role_menu.menu_id
from menu,
     role_menu
where role_menu.menu_id = menu.menu_id
  and role_menu.role_id = #{roleId}
